// import {db} from "./firebase-config"
import { useEffect, useState } from 'react';
import './App.css';
import firebase from "./firebase-config"
// import { db } from './firebase-config';
// import collection  from "firebase/firebase-firestore"

function App() {
  const [foods, setFoods] = useState([])
  const [loading, setLoading] = useState(false)

  // const ref = collection(firebase, "lunch")

  const lunchCollection = firebase.firestore().collection("lunch")
  console.log(lunchCollection)

  if (loading){
    return <h1>loading...</h1>
  }

  function getFoods(){
    setLoading(true)
    lunchCollection.onSnapshot((querysnapshot) => {
      const items = []
      querysnapshot.forEach(doc => {
        items.push(doc.data())
      })
      setFoods(items)
      setLoading(false)
    })
  }
  const displayFoods = foods.map(food => {
    return <div><p>{food.description}</p></div>
  })

  useEffect(() => {
    getFoods()
  })

  return (
    <div className="App">
      {displayFoods}

    </div>
  );
}

export default App;
