import React from "react";
import { Card, Button } from "react-bootstrap";

function FoodItem({name, description, image, price}){

    return(
        <Card className ="text-white border-dark bg-dark mb-3" style={{width :"19rem", margin: "8px"}}>
        <img className = "card-img-top" src={image} alt="Book Cover"/>
        <div class="card-body">
            <h5 class="card-title">{name}</h5>
            <p class="card-text">Kshs. {price}</p>
            <p class="card-text"><small>{description}</small></p>
            {/* <Button class="btn btn-primary">Show details</Button> */}
            <div class="card-footer mt-6">
                <Button type="button" id ="buybook" className="btn btn-primary mt-64">Add to Cart</Button>
            </div>

        </div>

    </Card>      

    )

}

export default FoodItem
